$(document).ready(function(){
  $('.turn').click(function(){
    var id = $(this).data("id");
    var active = $(this).text();
    alert(active);
    dataEquipment = {
      equipment:{
        active: true
      }
    }
    $.ajax({
        type: 'PATCH',
        url: "/equipment/"+id,
        data: dataEquipment,
        success: function(repsonse) {
          alert("Turn on is successfully.")
        },
        error: function(repsonse) {
          alert("Turn on is fails.")
        }
    })

  })
});
